-- Apenas dados para testes, por isso a versão do SQL alta, para ser sempre o último a ser executado










SELECT
    gs.lesson_number,
    DATEADD('DAY', -15 + (gs.lesson_number - 1) * 3, CURRENT_DATE),
    NULL,
    TRUE,
    id
FROM
    app.classrooms
CROSS JOIN SYSTEM_RANGE(1,20) AS gs(lesson_number);


    SELECT p.id, c.id, true
    FROM app.people p
    JOIN app.age_ranges ar
        ON TIMESTAMPDIFF(YEAR, p.birthdate, CURRENT_DATE) >= ar.min_age
        AND (ar.max_age IS NULL OR TIMESTAMPDIFF(YEAR, p.birthdate, CURRENT_DATE) <= ar.max_age)
    JOIN app.classrooms c on c.age_range_id = ar.id
    ORDER BY p.id, ar.id;


select
    CASE
        WHEN RAND() < 0.5 THEN TRUE
        ELSE FALSE
    END,
    true,
    s.id,
    l.id
from app.lessons l
join app.classrooms c on c.id = l.classroom_id
join app.students s on s.classroom_id = c.id
where l.lesson_date < CURRENT_DATE;






    SELECT p.person_id, c.id, true
    FROM app.classrooms c
    CROSS JOIN (
        select * from (values
            ('e106e90d-b875-4893-9c1c-f42858941304'),
            ('993fa0a7-f713-46cd-b500-4af4c2ae489c'),
            ('351c23a1-812e-463b-bb70-d9a076a52a3b'),
            ('3cb844b1-5c3e-462a-9380-20655fe78af8')
        ) as p(person_id)
    ) p
    ORDER BY RANDOM()
    LIMIT (select count(*) from app.classrooms);

select
    true,
    t.id,
    l.id
from app.lessons l
join app.classrooms c on c.id = l.classroom_id
join app.teachers t on t.classroom_id = c.id
where l.lesson_date < CURRENT_DATE;

